alb_ser_cod,alb_num, lac_num, alb_dia, lac_art_cod,lac_des,lac_uni,
round(lac_pru / alb_valdiveur,2) ,lac_dto,
lac_dto002,fac_dto,fac_dto002,far_cmiven,
round(lac_imp/alb_valdiveur,2) ,
round(lac_uni * lac_pru * ( 1 - nvl(lac_dto,0) * 0.01 ) * ( 1 - nvl(lac_dto002,0) * 0.01 )
* ( 1 - nvl(fac_dto,0) * 0.01 )* ( 1 - nvl(fac_dto002,0) * 0.01 ) /alb_valdiveur,2));


