CREATE OR REPLACE FORCE VIEW VGES_EMPSERREAL
AS 
select
  etp_idf_cod, etp_emp_cod, etp_prj_num, to_char(dia,'yyyy') fecha, uce_cod codi, uce_des capcalera, les_des ppr_des,
  decode(decode(instr(',' || fges_parametre(etp_idf_cod,'prjucecin', 'zzz<zzz') || ',',','||uce_cod||','),0,'D','I'), '1', 0, '2', 1, 'D', 2, 'I', 3) ordre,
  decode(instr(',' || fges_parametre(etp_idf_cod,'prjucecin', 'zzz<zzz') || ',',','||uce_cod||','),0,'D','I') tip,
  decode(decode(instr(',' || fges_parametre(etp_idf_cod,'prjucecin', 'zzz<zzz') || ',',','||uce_cod||','),0,'D','I'), '1', 'En Firme', '2', 'En Tramite', 'D', 'Coste Directo', 'I', 'Coste Indirecto') tipo,
  sum(decode(to_char(dia,'mm'),'01',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) enero,
  sum(decode(to_char(dia,'mm'),'02',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) febrero,
  sum(decode(to_char(dia,'mm'),'03',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) marzo,
  sum(decode(to_char(dia,'mm'),'04',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) abril,
  sum(decode(to_char(dia,'mm'),'05',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) mayo,
  sum(decode(to_char(dia,'mm'),'06',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) junio,
  sum(decode(to_char(dia,'mm'),'07',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) julio,
  sum(decode(to_char(dia,'mm'),'08',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) agosto,
  sum(decode(to_char(dia,'mm'),'09',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) setiembre,
  sum(decode(to_char(dia,'mm'),'10',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) octubre,
  sum(decode(to_char(dia,'mm'),'11',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) noviembre,
  sum(decode(to_char(dia,'mm'),'12',decode(fges_parametre(etp_idf_cod,'uce_cos'),2,imp,cos),0)) diciembre,
  'R' REAPREV, uce_cod, les_cod, pro_nif
from
  (
select
  dia, etp_prj_num, etp_idf_cod, etp_emp_cod, uce_cod, uce_des, les_cod, les_des, sum(imp) as imp, sum(cos) as cos, pro_nif
from
( select
    dia, etp_prj_num, etp_idf_cod, etp_emp_cod, v.uce_cod uce_cod,  v.uce_des uce_des,
    nvl(to_char(l.les_cod),'ZZZ-Sin asignar') as les_cod,
    nvl(les_des, 'ZZZ-Sin asignar') as les_des, imp, cos, pro_nif
  from
    vges_ucecosrea v, tges_les l, tges_pro, tges_alp t
  where
    les_idf_cod(+) = etp_idf_cod and
    les_emp_cod(+) = etp_emp_cod and
    les_prj_num(+) = etp_prj_num and
    les_etp_num(+) = etp_num and
	l.les_seq(+) = v.les_seq and
	alp_idf_cod(+) = etp_idf_cod and 
	alp_emp_cod(+) = etp_emp_cod and 
	t.alp_numdoc(+) = v.alp_numdoc and 
	pro_idf_cod(+) = alp_idf_cod and 
	pro_cod(+) = alp_pro_cod and
    uni <> 0
  union all
  select
    dia,
    etp_prj_num, etp_idf_cod, etp_emp_cod, 'ZZZ-Sin asignar' as uce_cod, 'ZZZ-Sin asignar' as uce_des,
	'ZZZ-Sin asignar' as les_cod, 'ZZZ-Sin asignar' as les_des, imp, cos, pro_nif
  from
    vges_ETPCOSNOA v, tges_pro, tges_alp t
  where
    uni <> 0 and alp_idf_cod(+) = etp_idf_cod and alp_emp_cod(+) = etp_emp_cod  and t.alp_numdoc(+) = v.alp_numdoc
    and pro_idf_cod(+) = alp_idf_cod and pro_cod(+) = alp_pro_cod
)
group by
  dia, etp_prj_num, etp_idf_cod,etp_emp_cod, uce_cod, uce_des, les_cod, les_des, pro_nif
order by dia,uce_cod, uce_des, les_cod, les_des
)
group by
  etp_prj_num, uce_des, les_des, etp_idf_cod, etp_emp_cod, uce_cod, to_char(dia,'yyyy'), uce_cod, les_cod,pro_nif
order by etp_prj_num, tip,  uce_cod, les_cod;


