CREATE OR REPLACE FORCE VIEW VGES_ETPCOSNOA
AS 
(  SELECT   'A' AS tip,
               etp_idf_cod,
               etp_emp_cod,
               etp_prj_num,
               etp_cod,
               etp_num,
               etp_des,
               etp_div_cod,
               etp_valdiveur,
               etp_diaini,
               etp_diafin,
               alb_prj_num AS prj,
               DECODE (alb_cls, '0', ' ', '*') || alb_ser_cod || '/' || alb_num
                  AS doc,
               alb_dia AS dia,
               alb_alp_numdoc AS alp_numdoc,
               lac_art_cod AS art,
               lac_des AS des,
               lac_unimet AS uni,
               fges_canvi_divisa (etp_idf_cod,
                                  lac_imp,
                                  alb_div_cod,
                                  etp_div_cod,
                                  alb_valdiveur,
                                  etp_valdiveur)
                  AS imp,
               fges_canvi_divisa (etp_idf_cod,
                                  lac_unimet * SUM (cal_imp),
                                  alb_div_cod,
                                  etp_div_cod,
                                  alb_valdiveur,
                                  etp_valdiveur)
                  AS COS
        FROM   tges_lac,
               tges_alb,
               tges_etp,
               tges_cal
       WHERE       lac_idf_cod = alb_idf_cod
               AND lac_emp_cod = alb_emp_cod
               AND lac_alb_numdoc = alb_numdoc
               AND alb_prj_num IS NOT NULL
               AND (lac_etp_cod is null or (lac_etp_cod is not null and lac_uce_seq is null))
               AND etp_idf_cod = alb_idf_cod
               AND etp_emp_cod = alb_emp_cod
               AND etp_prj_num = alb_prj_num
               AND alb_dia >= DECODE (etp_num, 1, alb_dia, etp_diaini)
               AND alb_dia <=
                     NVL (etp_diafin, TO_DATE ('31/12/5000', 'dd/mm/yyyy'))
               AND cal_idf_cod(+) = lac_idf_cod
               AND cal_emp_cod(+) = lac_emp_cod
               AND cal_alb_numdoc(+) = lac_alb_numdoc
               AND cal_lac_num(+) = lac_num
    GROUP BY   etp_idf_cod,
               etp_emp_cod,
               etp_prj_num,
               etp_cod,
               etp_num,
               etp_des,
               etp_div_cod,
               etp_valdiveur,
               etp_diaini,
               etp_diafin,
               alb_prj_num,
               alb_dia,
               alb_alp_numdoc,
               alb_cls,
               alb_ser_cod,
               alb_num,
               alb_div_cod,
               alb_valdiveur,
               alb_ref,
               lac_num,
               lac_seq,
               lac_art_cod,
               lac_des,
               lac_unimet,
               lac_imp)
   UNION ALL
   (/*
select 'P' as tip, etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, etp_des,etp_div_cod,
etp_valdiveur, etp_diaini, etp_diafin, lff_prj_num as prj,
'PARTE:' || ffo_ref as doc,eff_dat as dia , 0 as alp_numdoc,
eff_art_cod as art , max(lff_des) as des, eff_uni as uni,
decode(eff_tip,'O',(eff_uni * eff_pru), (eff_uni * eff_pru + eff_uni001 * eff_pru001 + eff_uni002 * eff_pru002 ) ) as imp,
decode(eff_tip,'O',(eff_uni * eff_pru), (eff_uni * eff_pru + eff_uni001 * eff_pru001 + eff_uni002 * eff_pru002 ) ) as cos
from tges_etp, trhu_lff, tges_eff, trhu_ffo
where
lff_idf_cod = etp_idf_cod and lff_emp_cod = etp_emp_cod and lff_prj_num = etp_prj_num
and lff_etp_cod  is null and lff_etp_num is null and lff_uce_seq is null and lff_prj_num is not null
and eff_idf_cod = lff_idf_cod and eff_ffo_cod = lff_ffo_cod and eff_prj_num = lff_prj_num
and nvl(eff_pre_cod,0)  = nvl(lff_pre_cod,0) and nvl(eff_cap_cod,'?') = nvl(lff_cap_cod,'?')
and nvl(eff_pda_cod,'?') = nvl(lff_pda_cod,'?')
and ffo_idf_cod = lff_idf_cod and ffo_cod = lff_ffo_cod
and eff_dat >= etp_diaini and eff_dat <= nvl(etp_diafin,'31/12/5000')
group by
etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, etp_des,etp_div_cod,
etp_valdiveur, etp_diaini, etp_diafin, lff_prj_num ,
ffo_ref ,eff_dat ,
eff_art_cod  , eff_uni ,
decode(eff_tip,'O',(eff_uni * eff_pru), (eff_uni * eff_pru + eff_uni001 * eff_pru001 + eff_uni002 * eff_pru002 ) ),
decode(eff_tip,'O',(eff_uni * eff_pru), (eff_uni * eff_pru + eff_uni001 * eff_pru001 + eff_uni002 * eff_pru002 ))
*/
    SELECT   'P' AS tip,
             etp_idf_cod,
             etp_emp_cod,
             etp_prj_num,
             etp_cod,
             etp_num,
             etp_des,
             etp_div_cod,
             etp_valdiveur,
             etp_diaini,
             etp_diafin,
             eff_prj_num AS prj,
             'PARTE:' || ffo_ref AS doc,
             eff_dat AS dia,
             0 AS alp_numdoc,
             eff_art_cod AS art,
             eff_des AS des,
             eff_uni AS uni,
             DECODE (
                eff_tip,
                'O',
                NVL (eff_uni * eff_pru, 0),
                (  NVL (eff_uni * eff_pru, 0)
                 + NVL (eff_uni001 * eff_pru001, 0)
                 + NVL (eff_uni002 * eff_pru002, 0))
             )
                AS imp,
             DECODE (
                eff_tip,
                'O',
                NVL (eff_uni * eff_pru, 0),
                (  NVL (eff_uni * eff_pru, 0)
                 + NVL (eff_uni001 * eff_pru001, 0)
                 + NVL (eff_uni002 * eff_pru002, 0))
             )
                AS COS
      FROM   tges_etp, tges_eff, trhu_ffo
     WHERE       eff_idf_cod = etp_idf_cod
             AND eff_emp_cod = etp_emp_cod
             AND eff_prj_num = etp_prj_num
and (eff_etp_cod is null or (eff_etp_cod is not null and eff_uce_seq is null))
             AND eff_prj_num IS NOT NULL
             AND eff_uni <> 0
             AND ffo_idf_cod(+) = eff_idf_cod
             AND ffo_cod(+) = eff_ffo_cod
             AND eff_dat >= DECODE (etp_num, 1, eff_dat, etp_diaini)
             AND eff_dat <=
                   NVL (etp_diafin, TO_DATE ('31/12/5000', 'dd/mm/yyyy')));


CREATE OR REPLACE FORCE VIEW VGES_ETPUCECOS
AS 
select etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, etp_des,etp_div_cod,
etp_valdiveur, etp_diaini, etp_diafin,uce_cod, uce_des,nvl(uce_cosreaant,0) uce_cosreaant,
sum(fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,1)) as uce_pdcant,
sum(fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,2)) as uce_pdcori,
sum(fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,3)) as uce_pdcexcori,
sum(fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,4)) as uce_pdcexcact,
--sum(nvl(least(  nvl(les_uniant,0) * nvl(les_prunet,0)  , nvl(les_prunet,0) * nvl(les_unietd,0)),0)) as uce_pdcant,
--sum( nvl(least( ( ( nvl(les_uniant,0) + nvl(les_uniact,0) ) * nvl(les_prunet,0))  , nvl(les_prunet,0) * nvl(les_unietd,0)),0) ) as uce_pdcori,
( nvl(uce_cosreaant,0) + nvl(uce_cosreaact,0)) as uce_cosreori,--cos,
--( nvl(uce_impiptant,0) + nvl(uce_impiptact,0)) as uce_cosreori,
nvl(sum(  ( nvl(les_uniact,0) *  nvl( les_cosuni,0) ) + nvl(les_cosant,0) ),0) as uce_costeo,
sum( nvl(les_cosant,0) ) as uce_costeoant,
sum( greatest(nvl(les_cosuni,0) * ( nvl(les_unietd,0) - nvl(les_uniact,0) - nvl(les_uniant,0) ),0) ) as uce_costeopen,
sum( greatest(nvl(les_cosuni,0) * ( nvl(les_unietd,0) - nvl(les_uniant,0) ),0) ) as uce_costeopenant,
sum( greatest(nvl(les_prunet,0) * ( nvl(les_unietd,0) - nvl(les_uniant,0) ),0) ) as uce_pdcpenant,
sum( greatest(nvl(les_prunet,0) * ( nvl(les_unietd,0) - nvl(les_uniant,0) - nvl(les_uniact,0)),0) ) as uce_pdcpen
from tges_etp, tges_uce, vges_les
where uce_idf_cod = etp_idf_cod and uce_emp_cod = etp_emp_cod
and uce_prj_num = etp_prj_num and uce_etp_cod = etp_cod and uce_etp_num = etp_num
and les_idf_cod = uce_idf_cod and les_emp_cod = uce_emp_cod and les_prj_num = uce_prj_num
and les_etp_cod = uce_etp_cod and les_etp_num = uce_etp_num and les_uce_seq = uce_seq
group by
etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, etp_des,etp_div_cod,
etp_valdiveur, etp_diaini, etp_diafin,uce_cod, uce_des,uce_cosreaant,uce_cosreaact,uce_impiptant,uce_impiptact
order by etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, uce_cod;


CREATE OR REPLACE FORCE VIEW VGES_ETPUCELES
AS 
select etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, etp_des,etp_div_cod,
etp_valdiveur, etp_diaini, etp_diafin,uce_cod, uce_des, les_seq,les_cod, les_art_cod,
les_des, les_desred, les_tun_cod,les_unietd, les_pru, les_prunet,
les_cosuni, ( nvl(les_unipre,0) * nvl(les_pru,0)) les_imp,
les_uniact,les_uniant, (nvl(les_uniant,0) + nvl(les_uniact,0)) les_uniori,
( nvl(les_uniact,0) * nvl(les_cosuni,0) ) les_cosact, les_cosant, ( ( nvl(les_uniact,0) * nvl(les_cosuni,0) ) + nvl(les_cosant,0) ) les_cosori,
least( ( nvl(les_unietd,0) -      least( nvl(les_uniant,0), nvl(les_unietd,0) )     ) , les_uniact ) * nvl(les_prunet,0)  as les_impact,
--(nvl(les_uniact,0)  * nvl(les_prunet,0) )  les_impact,
--( least( nvl(les_unipre,0),nvl(les_uniant,0)) * nvl(les_prunet,0)) les_impant,
fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,1) as les_impant,
fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,2) as les_impori,
--( least( nvl(les_unietd,0),nvl(les_uniant,0)) * nvl(les_prunet,0)) les_impant,
--( least( nvl(les_unietd,0), ((nvl(les_uniant,0) + nvl(les_uniact,0) ) ) ) * nvl(les_prunet,0) ) les_impori,
--( greatest( ( nvl(les_uniant,0) + nvl(les_uniact,0) - nvl(les_unietd,0) ), 0) * nvl(les_prunet,0) ) les_impexc,
fges_lesprod(etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num,les_seq,3) as les_impexc,
les_cosreaact, les_cosreaant,( nvl(les_cosreaact,0) + nvl(les_cosreaant,0)) les_cosreaori,
les_impiptact, les_impiptant, (nvl(les_impiptact,0) + nvl(les_impiptant,0)) les_impiptori
from tges_etp, tges_uce, vges_les
where uce_idf_cod = etp_idf_cod and uce_emp_cod = etp_emp_cod
and uce_prj_num = etp_prj_num and uce_etp_cod = etp_cod and uce_etp_num = etp_num
and les_idf_cod = uce_idf_cod and les_emp_cod = uce_emp_cod and les_prj_num = uce_prj_num
and les_etp_cod = uce_etp_cod and les_etp_num = uce_etp_num and les_uce_seq = uce_seq
order by etp_idf_cod, etp_emp_cod, etp_prj_num, etp_cod, etp_num, uce_cod,les_cod;


