            fac_idf_cod,
            fac_emp_cod,
            fac_ser_cod,
            fac_cls,
            fac_num,
            /*fac_empcmp,*/
            fac_exccmp,
            fac_dricmp,
            fac_asicmp
     FROM   (SELECT   'FAC' AS tip,
                      fac_idf_cod,
                      fac_emp_cod,
                      fac_ser_cod,
                      fac_cls,
                      fac_num,
                     /* ltrim(rtrim(substr(emp_codcmp,1,4))) as fac_empcmp,*/
                      fac_exccmp,
                      fac_dricmp,
                      fac_asicmp
               FROM   tges_fac, tges_emp
              WHERE   NVL (fac_asicmp, -1) > 0
                and emp_idf_cod=fac_idf_cod and emp_cod=fac_emp_cod
             UNION ALL
             -- movs de caixa
             SELECT   'MDC',
                      vcx_idf_cod,
                      vcx_emp_cod,
                      vcx_ser_cod,
                      vcx_fac_cls,
                      vcx_fac_num,
                      /*ltrim(rtrim(substr(emp_codcmp,1,4))) as fac_empcmp,*/
                      mdc_exccmp,
                      mdc_dricmp,
                      mdc_asicmp
               FROM   tges_mdc, tges_vcx, tges_emp
              WHERE       NVL (mdc_asicmp, -1) > 0
                      AND VCX_IDF_COD = mdc_idf_cod
                      AND VCX_EMP_COD = mdc_emp_cod
                      AND VCX_CXA_COD = mdc_cxa_cod
                      AND VCX_MDC_NUM = mdc_num
                and emp_idf_cod=mdc_idf_cod and emp_cod=mdc_emp_cod
