 PGES_DES.IVA (ALB.ALB_IDF_COD, ALB.ALB_IVA_COD) as IVA_DESCRIP,
 LAC.LAC_FCS as FACTOR_CONVERSION_SALIDA,
 LAC.LAC_UNI  as UNIDADES,
 LAC.LAC_PRU  as PRECIO,
 LAC.LAC_IMP  as IMPORTE,
 LAC.LAC_SEQ  as SECUENCIA,
 LAC.LAC_MOVSTO  as MOVIMIENTO_STOCK,
 LAC.LAC_DTO  as DESCUENTO,
 LAC.LAC_LPR_NUM  as LINEA_PRESUPUESTO,
 LPR_DES  as LINEA_PRESUP_DESCRIP,
 LAC.LAC_LOT  as LOTE,
 PGES_DES.PRE (ALB.ALB_IDF_COD, ALB.ALB_EMP_COD, ALB.ALB_PRE_COD)  as PRESUPUESTO,
 LAC.LAC_CAP_COD  as CAPITULO,
 PGES_DES.CAP (ALB.ALB_IDF_COD, ALB.ALB_EMP_COD, ALB.ALB_PRE_COD, ALB.ALB_CAP_COD) as CAPITULO_DESCRIP,
 LAC.LAC_PDA_COD as PARTIDA,
 PGES_DES.PDA (ALB.ALB_IDF_COD, ALB.ALB_EMP_COD, ALB.ALB_PRE_COD, ALB.ALB_CAP_COD, ALB.ALB_PDA_COD) as PARTIDA_DESCRIP,
 LAC.LAC_DTO002  as DESCUENTO2,
 LAC.LAC_EVS_COD  as ENVASE,
 EVS_DES  as ENVASE_DESCRIP,
 LAC.LAC_UNIEVS  as ENVASE_UNI,
 LAC.LAC_PRUEVS  as ENVASE_PRECIO,
 LAC.LAC_LLA  as LARGO,
 LAC.LAC_AMP  as ANCHO,
 LAC.LAC_ALR  as ALTO,
 LAC.LAC_ORD_COD  as ORDEN_NUMERO,
 ORD_DES  as ORDEN_DESCRIP,
 LAC.LAC_DIAFABCDC  as FECHA_FAB_CADUC,
 LAC.LAC_OBS  as OBSERVACIONES,
 LAC.LAC_RAP  as RAPPEL,
 LAC.LAC_TCS_PTE  as PORCIENTO_COMISION,
 LAC.LAC_UNIMET  as UNIDADES_METRICAS,
 LAC.IMP_MATERIAL as IMP_MATERIAL,
 LAC.UNI_MOBRA as UNI_MANO_OBRA,
 LAC.IMP_MOBRA as IMP_MANO_OBRA,
 LAC.COST_MATERIAL as COSTE_MATERIAL,
 LAC.COST_MOBRA as COSTE_MANO_OBRA
