CREATE OR REPLACE FORCE VIEW VGES_LACSUMELA
AS 
select
    alb_ser_cod,
    alb_num,
    alb_emp_cod,
    alb_idf_cod,
    alb_numdoc,
    alb_dia,
    ALB_SER_CODFAC, ALB_FAC_CLS, ALB_FAC_NUM,
    --
    lac_art_cod,
    lac_num,
    lac_seq,
    lac_imp,
    Fges_LacCost(alb_idf_cod, alb_emp_cod, alb_numdoc, lac_num,alb_div_cod, lac_art_cod, lac_unimet) lac_cos,
    SUM(lac_unimet * ela_unimet * ela_pvp * (1-NVL(lac_dto, 0) * 0.01) * (1-NVL(lac_dto002, 0) * 0.01)) ela_imp,
    SUM(lac_unimet * ela_unimet * ela_cos ) ela_cos
  from
   tges_alb, tges_lac, tges_ela
  where
  /* alb  -> lac */
  alb_idf_cod = lac_idf_cod and
  alb_emp_cod = lac_emp_cod and
  alb_numdoc = lac_alb_numdoc and
  /* lac -> ela */
  lac_idf_cod = ela_idf_cod  and
  lac_emp_cod = ela_emp_cod and
  lac_alb_numdoc = ela_alb_numdoc and
  lac_num = ela_lac_num
 group by
    alb_ser_cod,
    alb_num,
    alb_emp_cod,
    alb_idf_cod,
    alb_numdoc,
    alb_dia,
    ALB_SER_CODFAC, ALB_FAC_CLS, ALB_FAC_NUM,
    --
    lac_art_cod,
    lac_num,
    lac_seq,
    lac_imp,
    Fges_LacCost(alb_idf_cod, alb_emp_cod, alb_numdoc, lac_num,alb_div_cod, lac_art_cod, lac_unimet)
 having
   lac_imp <> SUM(lac_unimet * ela_unimet * ela_pvp * (1-NVL(lac_dto, 0) * 0.01) * (1-NVL(lac_dto002, 0) * 0.01)) OR
   Fges_LacCost(alb_idf_cod, alb_emp_cod, alb_numdoc, lac_num,alb_div_cod, lac_art_cod, lac_unimet) <>
   SUM( lac_unimet * ela_unimet * ela_cos );


