CREATE OR REPLACE FORCE VIEW VGES_LCE
AS 
SELECT
 LCE_IDF_COD, LCE_EMP_COD, LCE_PRE_COD, LCE_LPR_NUM, LCE_CER_NUM,
 LCE_UNIANT,  LCE_VALANT,  LCE_UNIACT,  LCE_PRU,     LCE_VALACT,
 LCE_DTE,     LCE_DTE002,  LCE_OBS,
 --
 LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD, LPR_NUM,
 LPR_ART_COD, LPR_SEQ,     LPR_DES,    LPR_CAP_COD,  LPR_PDA_COD,
 LPR_LLA,     LPR_AMP,     LPR_ALR,    LPR_REF,      LPR_UNIMET, LPR_UNI
FROM
 TGES_LCE, tges_lpr
WHERE
 lpr_idf_cod = lce_idf_cod and  --LPR-<LCE
 lpr_emp_cod = lce_emp_cod and
 lpr_pre_cod = lce_pre_cod and
 lpr_num = lce_lpr_num
UNION
SELECT
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, TO_NUMBER(NULL) LCE_LPR_NUM, CER_NUM,
 0 LCE_UNIANT, LCE_VALANT,  1 LCE_UNIACT,  LCE_VALACT, LCE_VALACT,
 TO_NUMBER(NULL) LCE_DTE,    TO_NUMBER(NULL) LCE_DTE002, TO_CHAR(NULL) LCE_OBS,
 --
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, TO_NUMBER(NULL) LCE_LPR_NUM,
 Fges_Parametre(cer_idf_cod, 'ART_ANC') LPR_ART_COD, TO_NUMBER(NULL) LPR_SEQ, APC_DES LPR_DES, TO_CHAR(NULL) LPR_CAP_COD, TO_CHAR(NULL) LPR_PDA_COD,
 TO_NUMBER(NULL) LPR_LLA, TO_NUMBER(NULL) LPR_AMP, TO_NUMBER(NULL) LPR_ALR, TO_CHAR(NULL) LPR_REF, TO_NUMBER(NULL) LPR_UNIMET, TO_NUMBER(NULL) LPR_UNI
FROM
 (
SELECT
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, CER_NUM, APC_DES,
 -SUM(DECODE(CER_NUM, APP_CER_NUM, 0, app_impseniva)) LCE_VALANT,
 -SUM(APP_IMPSENIVA) LCE_VALACT
FROM
 vges_appiva, tges_apc, tges_cer
WHERE
 cer_idf_cod = app_idf_cod AND
 cer_emp_cod = app_emp_cod AND
 cer_pre_cod = app_pre_codcer AND
 (
  (cer_ori = 'S' AND app_cer_num <= cer_num) OR (cer_ori = 'N' AND app_cer_num = cer_num)
 ) AND
 ---
 apc_idf_cod = app_idf_cod AND
 apc_emp_cod = APP_EMP_COD AND
 apc_pre_cod = APP_PRE_COD AND
 apc_num = APP_APC_NUM
GROUP BY
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, CER_NUM, APC_DES
 );


CREATE OR REPLACE FORCE VIEW VGES_LCECER
AS 
select tipo, lpr_idf_cod, lpr_emp_cod, lce_cer_num, lpr_pre_cod, pre_Ser_cod, pre_num, pre_Ver, 
  lpr_CAP_COD, CAP_DES, cap_seq, lpr_PDA_COD, PDA_DES, pda_seq, 
  LPR_ART_COD, esp_art_cod, lpr_des, LPR_LLA, LPR_AMP, LPR_ALR, LPR_SEQ, lpr_acc, lpr_num, 
  art_dim, art_fcs, art_pvp, art_marpvpcos, lpr_far_tip, lpr_uni, lpr_unimet, CosteUni, UniOri, ValOri, 
  CosteOri, UniNoOri, ValNoOri, CosteNoOri, UniPDCOri, ValPDCOri, CostePDCOri, UniPDCNoOri, ValPDCNoOri, 
  CostePDCNOOri, lpr_pru, LPR_DTO, LPR_DTO002, ImporteOri, ImporteNoOri, ImportePDCOri, ImportePDCNoOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteOri, 0), 0, null, round(((ImporteOri / CosteOri) -1) * 100,2)), 
         '2', case 
                when ImporteOri <> 0 then round(100 * (1 - (CosteOri / ImporteOri)),2) 
                when CosteOri <>0  then 0 
                when CosteOri = 0 and ImporteOri = 0 then null 
              end 
         ) MargenOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteNoOri, 0), 0, null, round(((ImporteNoOri / CosteNoOri) -1) * 100,2)), 
         '2', case 
                when ImporteNoOri <> 0 then round(100 * (1 - (CosteNoOri / ImporteNoOri)),2) 
                when CosteNoOri <>0  then 0 
                when CosteNoOri = 0 and ImporteNoOri = 0 then null 
              end 
         ) MargenNoOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteOri, 0), 0, null, round(((ImportePDCOri / CostePDCOri) -1) * 100,2)), 
         '2', case 
                when ImportePDCOri <> 0 then round(100 * (1 - (CostePDCOri / ImportePDCOri)),2) 
                when CostePDCOri <>0  then 0 
                when CostePDCOri = 0 and ImportePDCOri = 0 then null 
              end 
         ) MargenPDCOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteOri, 0), 0, null, round(((ImportePDCNoOri / CostePDCNoOri) -1) * 100,2)), 
         '2', case 
                when ImportePDCNoOri <> 0 then round(100 * (1 - (CostePDCNoOri / ImportePDCNoOri)),2) 
                when CostePDCNoOri <>0  then 0 
                when CostePDCNoOri = 0 and ImportePDCNoOri = 0 then null 
              end 
         ) MargenPDCNoOrI 
from 
( 
  select tipo, lpr_idf_cod, lpr_emp_cod, lce_cer_num, lpr_pre_cod, pre_Ser_cod, pre_num, pre_Ver, lpr_CAP_COD, CAP_DES, cap_seq, lpr_PDA_COD, PDA_DES, pda_seq, 
  LPR_ART_COD, esp_art_cod, lpr_des, LPR_LLA, LPR_AMP, LPR_ALR, LPR_SEQ, lpr_acc, lpr_num, 
  art_dim, art_fcs, art_pvp, art_marpvpcos, lpr_far_tip, lpr_uni, lpr_unimet, CosteUni, UniOri, ValOri, 
  decode 
  ( 
    fges_parametre(lpr_idf_cod, 'STO_DIM', 'N'), 
    'N', round(UniOri*CosteUni, 2), 
    'S', round(UniOri*CosteUni/* *nvl(lpr_lla * fges_parametre(lpr_idf_cod, 'lla_fc', 1), 1) * 
                               nvl(lpr_amp * fges_parametre(lpr_idf_cod, 'amp_fc', 0.01), 1) * 
                               nvl(lpr_alr * fges_parametre(lpr_idf_cod, 'alr_fc', 0.001), 1)*/, 2) 
  ) CosteOri, 
   UniNoOri, ValNoOri, 
  decode 
  ( 
    fges_parametre(lpr_idf_cod, 'STO_DIM', 'N'), 
    'N', round(UniNoOri*CosteUni, 2), 
    'S', round(UniNoOri*CosteUni/**nvl(lpr_lla * fges_parametre(lpr_idf_cod, 'lla_fc', 1), 1) * 
                                 nvl(lpr_amp * fges_parametre(lpr_idf_cod, 'amp_fc', 0.01), 1) * 
                                 nvl(lpr_alr * fges_parametre(lpr_idf_cod, 'alr_fc', 0.001), 1)*/, 2) 
  ) CosteNoOri, 
  UniPDCOri, ValPDCOri, 
  decode 
  ( 
    fges_parametre(lpr_idf_cod, 'STO_DIM', 'N'), 
    'N', round(UniPDCOri*CosteUni, 2), 
    'S', round(UniPDCOri*CosteUni/**nvl(lpr_lla * fges_parametre(lpr_idf_cod, 'lla_fc', 1), 1) * 
                               nvl(lpr_amp * fges_parametre(lpr_idf_cod, 'amp_fc', 0.01), 1) * 
                               nvl(lpr_alr * fges_parametre(lpr_idf_cod, 'alr_fc', 0.001), 1)*/, 2) 
  ) CostePDCOri, 
  UniPDCNoOri, ValPDCNoOri, 
  decode 
  ( 
    fges_parametre(lpr_idf_cod, 'STO_DIM', 'N'), 
    'N', round(UniPDCNoOri*CosteUni, 2), 
    'S', round(UniPDCNoOri*CosteUni/**nvl(lpr_lla * fges_parametre(lpr_idf_cod, 'lla_fc', 1), 1) * 
                                    nvl(lpr_amp * fges_parametre(lpr_idf_cod, 'amp_fc', 0.01), 1) * 
                                    nvl(lpr_alr * fges_parametre(lpr_idf_cod, 'alr_fc', 0.001), 1)*/, 2) 
  ) CostePDCNOOri, 
  lpr_pru, LPR_DTO, LPR_DTO002, 
  round(ValOri      * (1-nvl(pre_dte, 0)/100) * (1-nvl(pre_dte002, 0)/100), 2) ImporteOri, 
  round(ValNoOri    * (1-nvl(pre_dte, 0)/100) * (1-nvl(pre_dte002, 0)/100), 2) ImporteNoOri, 
  round(ValPDCOri   * (1-nvl(pre_dte, 0)/100) * (1-nvl(pre_dte002, 0)/100), 2) ImportePDCOri, 
  round(ValPDCNoOri * (1-nvl(pre_dte, 0)/100) * (1-nvl(pre_dte002, 0)/100), 2) ImportePDCNoOri 
  from 
  ( 
    -- tonir: feim aquest select from perque quedi mes clara la vista perque no faria falta pero per no arrastrar tot el codi que 
    -- obte el cost unitari queda me facil d'entendre i manejar aix 
    SELECT '' Tipo, lpr_idf_cod, lpr_emp_cod, lce_cer_num, lpr_pre_cod, pre_Ser_cod, pre_num, pre_Ver, lpr_CAP_COD, CAP_DES, cap_seq, lpr_PDA_COD, PDA_DES, pda_seq, 
    LPR_ART_COD, null esp_art_cod, lpr_des, LPR_LLA, LPR_AMP, LPR_ALR, round((LPR_PRU * lpr_unimet)/lpr_uni, 2) lpr_pru,  LPR_SEQ, lpr_acc, pre_dte, pre_dte002, 
    LPR_DTO, LPR_DTO002, lpr_num, art_dim, art_fcs, art_pvp, art_marpvpcos, 
    far_tip as lpr_far_tip, lpr_uni, lpr_unimet, 
    nvl(LCE_UNIACT,0) UniOri, nvl(LCE_VALACT,0) ValOri, 
    nvl(LCE_UNIACT,0) - nvl(LCE_UNIANT,0) UniNoOri, 
    nvl(LCE_VALACT,0) - nvl(LCE_VALANT,0) ValNoOri, 
    nvl(LCE_UNIPDCACT,0) UniPDCOri, nvl(LCE_VALPDCACT,0) ValPDCOri, 
    nvl(LCE_UNIPDCACT,0) - nvl(LCE_UNIPDCANT,0) UniPDCNoOri, 
    nvl(LCE_VALPDCACT,0) - nvl(LCE_VALPDCANT,0) ValPDCNoOri, 
    round(( decode( 
            fges_parametre(lpr_idf_cod, 'CALC_MARGEN_PRE', '1'), 
            '1', 
               (select sum(nvl(cpr_imp,0)) from tges_cpr where cpr_idf_cod = lpr_idf_cod and cpr_emp_cod = lpr_emp_cod and 
               cpr_pre_cod = lpr_pre_cod and cpr_lpr_num = lpr_num), 
             decode ( 
                      fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
                      '1', 
                      round(fges_canvi_divisa(lpr_idf_cod, art_pvp, pre_div_cod, 'EURO'), 2) / (1 + art_marpvpcos * 0.01), 
                      '2', 
                      round(fges_canvi_divisa(lpr_idf_cod, art_pvp, pre_div_cod, 'EURO'), 2) * (1 - art_marpvpcos * 0.01) 
                    ) 
            ) * lpr_unimet 
       ) / lpr_uni,2) CosteUni 
    FROM TGES_CAP, TGES_PDA, TGES_LPR, tges_art, tges_iva, tges_far, tges_lce, tges_pre 
    where lce_idf_cod = lpr_idf_cod and lce_emp_cod = lpr_emp_cod and lce_pre_cod = lpr_pre_cod and lce_lpr_num = lpr_num 
    and art_idf_cod = lpr_idf_cod and art_cod = lpr_art_cod and iva_idf_cod = art_idf_cod and iva_cod = art_iva_cod 
    and art_idf_cod=far_idf_cod and art_far_cod=far_cod 
    and lpr_idf_cod = pda_idf_cod (+) and lpr_emp_cod = pda_emp_cod (+) and lpr_pre_cod = pda_pre_cod (+) 
    and lpr_cap_cod = pda_cap_cod (+) and lpr_pda_cod = pda_cod (+) and pda_idf_cod = cap_idf_cod (+) 
    and pda_emp_cod = cap_emp_cod (+) and pda_pre_cod = cap_pre_cod (+) and pda_cap_cod = cap_cod (+) 
    and lpr_idf_cod = art_idf_cod and lpr_art_cod = art_cod and art_idf_cod = far_idf_cod and art_far_cod = far_cod 
    and pre_idf_cod = lpr_idf_cod and pre_emp_cod = lpr_emp_cod and pre_cod = lpr_pre_cod-- and 1=0 
    union all 
    SELECT 'E' Tipo, lpr_idf_cod, lpr_emp_cod, lce_cer_num, lpr_pre_cod, pre_Ser_cod, pre_num, pre_Ver, lpr_CAP_COD, CAP_DES, cap_seq, lpr_PDA_COD, PDA_DES, pda_seq, 
    LPR_ART_COD, esp_art_cod, art_des lpr_des, esp_LLA, esp_AMP, esp_ALR, esp_pvp LPR_PRU, LPR_SEQ, lpr_acc, pre_dte, pre_dte002, 
    LPR_DTO, LPR_DTO002, lpr_num, art_dim, art_fcs, art_pvp, art_marpvpcos, far_tip as lpr_far_tip, lpr_uni, lpr_unimet, 
     nvl(LCE_UNIACT,0)*esp_unimet UniOri, 
    nvl(LCE_UNIACT,0)*esp_unimet * ESP_PVP ValOri, 
    ROUND((nvl(LCE_UNIACT,0) - nvl(LCE_UNIANT,0))*esp_unimet, 2) UniNoOri, 
    ROUND((nvl(LCE_UNIACT,0) - nvl(LCE_UNIANT,0))*esp_unimet * ESP_PVP,2) ValNoOri, 
    ROUND(nvl(LCE_UNIPDCACT,0)*esp_unimet,2) UniPDCOri, 
    ROUND(nvl(LCE_UNIPDCACT,0)*esp_unimet * ESP_PVP,2) ValPDCOri, 
    ROUND((nvl(LCE_UNIPDCACT,0) - nvl(LCE_UNIPDCANT,0))*esp_unimet,2) UniPDCNoOri, 
    ROUND((nvl(LCE_UNIPDCACT,0) - nvl(LCE_UNIPDCANT,0))*esp_unimet * ESP_PVP,2) ValPDCNoOri, 
