  LPR_ACC,	LPR_SEQ,      LPR_DTO,		LPR_DTO002,   ESP_LLA,	    ESP_AMP,	   ESP_ALR,	   ESP_OBS,
  ESP_REF,	LPR_DIAPRTINI,LPR_DIAPRTFIN,    LPR_EST,      LPR_OPE_COD,  TO_CHAR(NULL), TO_NUMBER(NULL), TO_NUMBER(NULL),
  LPR_TCS_PTE,	 LPR_DIAACC,   LPR_DIAPRTLLI,   LPR_UNIMETSTO,ESP_REF2,     ESP_NUM,
  LPR_UNI * ESP_UNIMET,
  LPR_UNI * ESP_UNI,
  LPR_UNI * ESP_UNIMET * ESP_PVP * (1-NVL(LPR_DTO, 0) * 0.01) * (1-NVL(LPR_DTO002, 0) * 0.01) LPR_IMP,
  NVL(ART_GRPFAC, 'N') ART_GRPFAC,
  ART_DESCUR LPR_GRPDES,
  ART_STO LPR_ART_STO, ART_FAR_COD, ESP_COS, ART_PRDFRE, LPR_ART_COD LPR_ART_CODLPR, lpr_insini
FROM
  TGES_LPR,
  TGES_ESP,
  TGES_ART
WHERE
  /* LPR -< ESP */
  LPR_IDF_COD = ESP_IDF_COD AND
  LPR_EMP_COD = ESP_EMP_COD AND
  LPR_PRE_COD = ESP_PRE_COD AND
  LPR_NUM = ESP_LPR_NUM AND
  /* ART -< ESP */
  ART_IDF_COD = ESP_IDF_COD AND
  ART_COD = ESP_ART_COD AND
  ART_BLO = 'N';


CREATE OR REPLACE FORCE VIEW VGES_LSTAVRPENALB
