from trma_inr 
where inr_alb_numdoc is not null and not exists (select 1 from tges_alb where alb_idf_cod=inr_idf_cod and alb_emp_cod=inr_emp_cod and alb_numdoc=inr_alb_numdoc) 
and inr_tipent NOT in ('BM', 'EM', 'SM') 
union all 
/*Divergencies intervals creats per obertures manuals de les barreres*/ 
select 'D' TipoReg, 3 tipo, INR_IDF_COD, INR_COD, INR_DAT, INR_TIPENT, 
decode(INR_TIPENT, 'SM', 'Salida manual', 'EM', 'Entrada manual', 'BM', 'Bscula manual', 'CP', 'Carga previa') TipoEntrada, 
