CREATE OR REPLACE FORCE VIEW VGES_NTFVENPENCOB
AS 
SELECT   ven_idf_cod,
            ven_emp_cod,
            ven_fac_cls,
            ven_ser_cod,
            ven_Fac_num,
            ven_num,
            fac_dia,
            DECODE (VEN_FAC_CLS, 1, '**', '')
            || ' '
            || ven_ser_cod
            || '/'
            || ven_Fac_num
               Factura,
            ven_dia,
            ven_imp,
            NVL (fges_venimppendcob (ven_idf_cod,
                                     ven_emp_cod,
                                     ven_ser_cod,
                                     ven_Fac_num,
                                     ven_fac_cls,
                                     ven_num), ven_imp) PendCob,
            fac_cli_Cod,
            cli_nomfis,
            cli_tipmsg,
            NVL (ven_cntenv, 0) ven_cntenv,
            NVL (ven_cntenv, 0) + 1 ven_cntnextenv,
            NVL (dpc_eml, NVL (CLI_EMLFAC, cli_eml)) cli_eml,
            FLIM_GET_TOKEN (dpc_tel001, ' ', 1) dpc_tel001,
            FLIM_GET_TOKEN (cli_tel, ' ', 1) cli_tel,
            ven_dia
            + TO_NUMBER(NVL (
                           FLIM_GET_TOKEN (
                              fges_parametre (ven_idf_cod,
                                              'MDCNDIASNOTIF',
                                              '0'),
                              ';',
                              NVL (VEN_CNTENV, 0) + 1
                           ),
                           0
                        ))
               FechaVencReal,
            fac_scl_cod,
            SCL_EMLFAC,
            scl_nom,
            FLIM_GET_TOKEN (scl_tel, ' ', 1) scl_tel
     FROM   tges_ven,
            tges_Fac,
            tges_cli,
            tges_dpc,
            tges_scl,
            tges_emp
    WHERE       fac_idf_cod = ven_idf_cod
            AND fac_Emp_cod = ven_emp_cod
            AND fac_cls = ven_fac_cls
            AND fac_ser_cod = ven_ser_cod
            AND fac_num = ven_Fac_num
            AND cli_idf_cod = fac_idf_cod
            AND cli_cod = fac_cli_cod
            AND scl_idf_cod(+) = fac_idf_cod
            AND scl_cli_cod(+) = fac_cli_cod
            AND scl_cod(+) = fac_scl_cod
            AND emp_idf_cod = ven_idf_cod
            AND emp_cod = ven_emp_cod
            AND NVL (cli_tipmsg, 'N') <> 'N'
            AND dpc_idf_cod(+) = fac_idf_Cod
            AND dpc_cli_cod(+) = fac_cli_cod
            AND dpc_cod(+) =
                  UPPER (fges_parametre (fac_idf_cod, 'CLIDEPSMS', 'SMS'))
            AND NVL (fges_venimppendcob (ven_idf_cod,
                                         ven_emp_cod,
                                         ven_ser_cod,
                                         ven_Fac_num,
                                         ven_fac_cls,
                                         ven_num), 1) <> 0
            AND TO_DATE (TO_CHAR (SYSDATE, 'dd/mm/yyyy'), 'dd/mm/yyyy') >
                  ven_dia
                  + TO_NUMBER(LTRIM(NVL (
                                       FLIM_GET_TOKEN (
                                          fges_parametre (ven_idf_cod,
                                                          'MDCNDIASNOTIF',
                                                          '0'),
                                          ';',
                                          NVL (VEN_CNTENV, 0) + 1
                                       ),
                                       0
                                    )))
            AND NVL (ven_cntenv, 0) <
                  TO_NUMBER(FLIM_TOTALTOKENS (
                               fges_parametre (ven_idf_cod,
                                               'MDCNDIASNOTIF',
                                               '0'),
                               ';'
                            ))
            AND NVL (emp_envntf, 'N') = 'S';


CREATE OR REPLACE FORCE VIEW VGES_OBRCUR
AS 
( 
SELECT '1' AS tip, pre_idf_cod AS idf_cod, pre_emp_cod AS emp_cod , NVL(prj_num,'ZZZZZZZ') AS prj_num, 
 NVL(prj_nom, 'Sin Proyecto') AS prj_nom, NVL(prj_est,1) AS prj_est, 
pre_cli_cod AS cli_cod, pre_scl_cod AS scl_cod, cli_nomcom, 
pre_ser_cod, pre_num, pre_ver, DECODE(pre_ser_cod,NULL,NULL,pre_ser_cod||'\'||pre_num||'\'||pre_ver) AS presup,pre_est,'2' AS cls, 
Fges_Canvi_Divisa(pre_idf_cod,Fges_Impfac_Pre(pre_idf_cod,pre_emp_cod,pre_cod),pre_div_cod,NVL(plim_contenidor.get_var1,'EURO'),pre_valdiveur) AS pre_imp, 
0.0 AS servit, '' AS alb_dti,0.0 AS impfac, 0.0 AS impfactot , 0.0 AS cosservit, 0.0 AS cosfacturat, 0.0 AS cosfracer 
FROM tges_prj, tges_cli, tges_pre 
WHERE cli_idf_cod = pre_idf_cod AND cli_cod = pre_cli_cod 
AND pre_idf_cod =  prj_idf_cod(+) AND pre_emp_cod = prj_emp_cod(+) AND pre_prj_num = prj_num(+) 
GROUP BY pre_idf_cod, pre_emp_cod, prj_num,prj_nom,prj_est, pre_cli_cod, pre_scl_cod, cli_nomcom, 
pre_idf_cod,pre_emp_cod,pre_ser_cod, pre_num,pre_ver, pre_cod, pre_est, pre_div_cod,prj_div_cod,pre_valdiveur 
UNION ALL 
SELECT '2' AS tip, alb_idf_cod AS idf_cod, alb_emp_cod AS emp_cod ,  NVL(prj_num,'ZZZZZZZ') AS prj_num, NVL(prj_nom, 'Sin Proyecto') AS prj_nom, NVL(prj_est,1) AS prj_est, 
alb_cli_cod AS cli_cod, alb_scl_cod AS scl_cod, cli_nomcom, 
pre_ser_cod, pre_num, pre_ver, DECODE(pre_ser_cod,NULL,NULL,pre_ser_cod||'\'||pre_num||'\'||pre_ver) AS presup,pre_est, alb_cls AS cls, 
0 AS pre_imp, 
SUM(Fges_Canvi_Divisa(pre_idf_cod,lac_imp,alb_div_cod,NVL(plim_contenidor.get_var1,'EURO'),alb_valdiveur)) AS servit,alb_dti,0 AS impfac, 0 AS impfactot, 
SUM(Fges_Canvi_Divisa(alb_idf_cod,Fges_Laccost(lac_idf_cod, lac_emp_cod, lac_alb_numdoc, lac_num, alb_div_cod, lac_art_cod, lac_unimet),alb_div_cod,NVL(plim_contenidor.get_var1,'EURO'),alb_valdiveur)) AS cosservit, 
 0 AS cosfacturat, 0.0 AS cosfracer 
FROM tges_alb,tges_lac, tges_prj, tges_cli, tges_pre 
WHERE 
( alb_idf_cod = pre_idf_cod AND alb_emp_cod = pre_emp_cod AND alb_pre_cod = pre_cod 
AND alb_ser_cod <> flim_get_token(Fges_Parametre(alb_idf_cod,alb_emp_cod ||'_SERANC'),',',1) AND alb_dia <= TO_DATE(NVL(plim_contenidor.get_var2,'31/12/9999'),'dd/mm/yyyy') ) 
AND lac_idf_cod = alb_idf_Cod AND lac_emp_cod = alb_emp_cod AND lac_alb_numdoc = alb_numdoc 
AND prj_idf_cod(+) = alb_idf_cod AND prj_emp_cod(+) = alb_emp_cod AND prj_num(+) = alb_prj_num 
AND cli_idf_cod = alb_idf_cod AND cli_cod = alb_cli_cod AND alb_fbl = 'S' 
