) 
UNION ALL 
( 
SELECT '3' AS tip, pre_idf_cod, pre_emp_cod, NVL(prj_num, 'ZZZZZZZ') AS prj_num, NVL(prj_nom, 'Sin Proyecto') AS prj_nom, NVL(prj_est,1) AS prj_est, pre_cli_cod AS cli_cod, pre_scl_cod AS scl_cod, cli_nomcom, 
pre_ser_cod, pre_num, pre_ver, DECODE(pre_ser_cod,NULL,NULL,pre_ser_cod||'\'||pre_num||'\'||pre_ver) AS presup,pre_est, fac_cls AS cls, 0 AS pre_imp, 
0 AS servit, '' AS alb_dti,SUM(Fges_Canvi_Divisa(fac_idf_cod,Fges_Impfac(fac_idf_cod,fac_emp_cod,fac_ser_cod,fac_num,fac_cls),fac_div_cod,NVL(plim_contenidor.get_var1,'EURO'),fac_valdiveur)) AS impfac, 
SUM(Fges_Canvi_Divisa(fac_idf_cod,fac_bim,fac_div_cod,NVL(plim_contenidor.get_var1,'EURO'),fac_valdiveur)) AS impfactot, 
