GROUP BY pre_idf_cod, pre_emp_cod, prj_num, prj_nom, prj_est, pre_cli_cod, pre_scl_cod, cli_nomcom, 
pre_idf_cod,pre_emp_cod,pre_ser_cod, pre_num,pre_ver, pre_cod, pre_est, fac_cls 
) 
