( 
SELECT '4' AS tip, alb_idf_cod AS idf_cod,alb_emp_cod AS emp_cod,NVL(alb_prj_num,'ZZZZZZZ') AS prj_num, NVL(prj_nom,'Sin Proyecto') AS prj_nom,NVL(prj_est,1) AS prj_est, 
