CREATE OR REPLACE FORCE VIEW VGES_PLNWEB
AS 
SELECT   idf, emp, Tipo, Doc, Des, cli, clinom, scl, sclnom,
            Fecha, FecPrevista,
            NVL (accdom,  NVL (domscl, DOMCLI)) domcli,
            nvl (doc_lat, NVL (acclat, NVL (scl_lat, cli_lat))) lat,
            nvl (doc_lon, NVL (acclon, NVL (scl_lon, cli_lon))) lon,
            NVL (acccpo,  NVL (scl_cpo_cod, cli_cpo_cod)) cpo,
            urg, tel, percon, Planificacion, ZonaTrabajo, doc_lat, doc_lon
     FROM   (SELECT   avr_idf_cod idf,
                      avr_emp_cod emp,
                      'AVR' Tipo,
                      TO_CHAR (avr_num) Doc,
                      avr_desavr Des,
                      avr_cli_cod cli,
                      cli_nomcom clinom,
                      avr_scl_cod scl,
                      scl_nom sclnom,
                      avr_diaavi Fecha,
                      AVR_DIASOLENT FecPrevista,
                      CLI_DOMFIS DOMCLI,
                      scl_dom DOMSCL,
                      avr_dom accdom,
                      cli_lat,
                      scl_lat,
                      cli_lon,
                      scl_lon,
                      (SELECT acc_lat
                         FROM tges_acc
                        WHERE acc_idf_cod = avr_idf_cod
                              AND acc_cli_cod = avr_cli_cod
                              AND acc_cod = avr_acc_cod) acclat,
                         avr_lat doc_lat,
                      (SELECT acc_lon
                         FROM tges_acc
                        WHERE acc_idf_cod = avr_idf_cod
                              AND acc_cli_cod = avr_cli_cod
                              AND acc_cod = avr_acc_cod)
                         acclon,
                         avr_lon doc_lon,
                      avr_tel tel,
                      NVL (
                         (SELECT acc_con
                            FROM tges_acc
                           WHERE acc_idf_cod = avr_idf_cod
                                 AND acc_cli_cod = avr_cli_cod
                                 AND acc_cod = avr_acc_cod),
                         avr_nom
                      ) PerCon,
                      cli_cpo_cod,
                      scl_cpo_cod,
                      avr_cpo_cod accCpo,
                      NULL urg,
                      avr_vad_cod002 Planificacion,
                      avr_vad_cod003 ZonaTrabajo
               FROM   tges_avr, tges_cli, tges_scl
              WHERE       cli_idf_cod = avr_idf_cod
                      AND cli_cod = avr_cli_cod
                      AND scl_idf_cod(+) = avr_idf_cod
                      AND scl_cli_cod(+) = avr_cli_cod
                      AND scl_cod(+) = avr_scl_cod
                      AND avr_diafin IS NULL
             UNION ALL
             SELECT   avi_idf_cod idf,
                      avi_emp_cod emp,
                      'AVI' Tipo,
                      TO_CHAR (avi_num) Doc,
                      avi_descur Des,
                      avi_cli_cod cli,
                      cli_nomcom clinom,
                      avi_scl_cod scl,
                      scl_nom sclnom,
                      AVI_DATENT Fecha,
                      AVI_DATPRT FecPrevista,
                      CLI_DOMFIS DOMCLI,
                      scl_dom DOMSCL,
                      avi_dom accdom,
                      cli_lat,
                      scl_lat,
                      cli_lon,
                      scl_lon,
                      (SELECT   acc_lat
                         FROM   tges_acc
                        WHERE       acc_idf_cod = avi_idf_cod
                                AND acc_cli_cod = avi_cli_cod
                                AND acc_cod = avi_acc_cod)
                         acclat,
                         avi_lat doc_lat,
                      (SELECT   acc_lon
                         FROM   tges_acc
                        WHERE       acc_idf_cod = avi_idf_cod
                                AND acc_cli_cod = avi_cli_cod
                                AND acc_cod = avi_acc_cod)
                         acclon,
                         avi_lon doc_lon,
                      avi_tel tel,
                      (SELECT   acc_con
                         FROM   tges_acc
                        WHERE       acc_idf_cod = avi_idf_cod
                                AND acc_cli_cod = avi_cli_cod
                                AND acc_cod = avi_acc_cod)
                         percon,
                      cli_cpo_cod,
                      scl_cpo_cod,
                      avi_cpo_cod accCpo,
                      NULL urg,
                      avi_vad_cod Planificacion,
                      avi_vad_cod002 ZonaTrabajo
               FROM   tges_avi, tges_cli, tges_scl
              WHERE       cli_idf_cod = avi_idf_cod
                      AND cli_cod = avi_cli_cod
                      AND scl_idf_cod(+) = avi_idf_cod
                      AND scl_cli_cod(+) = avi_cli_cod
                      AND scl_cod(+) = avi_scl_cod
                      AND NVL (Avi_EST, 1) <> 3
             UNION ALL
             SELECT   pre_idf_cod idf,
                      pre_emp_cod emp,
                      'PRE' Tipo,
                      pre_ser_cod || '/' || pre_num || '/' || pre_ver Doc,
                      pre_ref Des,
