and cli_idf_cod = pre_idf_cod and cli_cod = pre_cli_cod
and art_idf_cod = lpr_idf_cod and art_cod = lpr_art_cod and art_cst = 'N'
and far_idf_cod = art_idf_cod and  far_cod = art_far_cod and far_tip = 'O' and lpr_uni <> 0 
and pre_est = 'A' and lpr_acc = 'S' 
and ope_idf_cod = pre_idf_cod and ope_cod = art_partxt004
group by pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_Ver,
pre_ser_cod || '/' || pre_num || '/' || pre_ver, pre_ref, pre_cli_cod, cli_nomcom, pre_scl_cod, pre_acc_cod, cli_zon_cod, pre_prj_num, pre_ope_cod,
lpr_des, lpr_cap_cod, lpr_pda_cod, art_partxt004, art_partxt005, ope_eml
