CREATE OR REPLACE FORCE VIEW VGES_PRJMESCOS
AS 
(
select 'A' as tip,prj_idf_cod, prj_emp_cod, prj_num,prj_nom, prj_ope_cod,prj_div_cod,
prj_valdiveur,prj_est,decode(alb_cls,'0',' ','*')||  alb_ser_cod || '/' || alb_num as doc,alb_dia as dia, to_char(alb_dia,'YYYYMM') as anymes,alb_alp_numdoc as alp_numdoc,
