fges_canvi_divisa(prj_idf_cod,lac_imp,alb_div_cod, prj_div_cod, alb_valdiveur, prj_valdiveur) as imp,
fges_canvi_divisa(prj_idf_cod,lac_unimet * SUM(cal_imp),alb_div_cod, prj_div_cod, alb_valdiveur, prj_valdiveur) as cos
from tges_lac, tges_alb, tges_prj, tges_cal
where
lac_idf_cod = alb_idf_cod and lac_emp_cod = alb_emp_cod and lac_alb_numdoc = alb_numdoc
and prj_idf_cod = alb_idf_cod and prj_emp_cod = alb_emp_cod and prj_num = alb_prj_num
and cal_idf_cod(+) = lac_idf_cod and cal_emp_cod(+) = lac_emp_cod and cal_alb_numdoc(+) = lac_alb_numdoc
and cal_lac_num(+) = lac_num
and alb_num is not null
group by prj_idf_cod, prj_emp_cod, prj_num,prj_nom, prj_ope_cod, prj_div_cod,prj_valdiveur,prj_est,
alb_prj_num,alb_dia,alb_alp_numdoc,alb_cls,alb_ser_cod,alb_num, alb_div_cod,alb_valdiveur,alb_ref,
lac_num, lac_seq, lac_art_cod, lac_des , lac_unimet, lac_imp
)
union all
(
select 'P' as tip, prj_idf_cod, prj_emp_cod, prj_num,prj_nom,prj_ope_cod, prj_div_cod,
prj_valdiveur,prj_est, 'PARTE:' || ffo_ref as doc,eff_dat as dia , to_char(eff_dat,'YYYYMM') as anymes,0 as alp_numdoc,
eff_art_cod as art , eff_des as des, eff_uni as uni,
decode(eff_tip,'O',(eff_uni * eff_pru), (eff_uni * eff_pru + eff_uni001 * eff_pru001 + eff_uni002 * eff_pru002 ) ) as imp,
decode(eff_tip,'O',(eff_uni * eff_pru), (eff_uni * eff_pru + eff_uni001 * eff_pru001 + eff_uni002 * eff_pru002 ) ) as cos
from tges_prj, tges_eff, trhu_ffo
where
eff_idf_cod = prj_idf_cod and eff_emp_cod = prj_emp_cod and eff_prj_num = prj_num
and  eff_prj_num is not null
and eff_uni <> 0
and ffo_idf_cod = eff_idf_cod and ffo_cod = eff_ffo_cod  and fges_parametre(prj_idf_cod,'CSTFFO','N')='S'
);


