CREATE OR REPLACE FORCE VIEW VGES_PRJPRV
AS 
select idf_cod, emp_cod, prj_num, anymes, sum(cos_mat) as cos_mat, sum(cos_mob) as cos_mob,sum(cost_prv_mat) as cost_prv_mat,
sum(cos_prv_mob) as cos_prv_mob, sum(prv_fac) as prv_fac,sum(pre_cob) as pre_cob,sum(ava_pre) as ava_pre,
sum(ava_est) as ava_est, sum(fac_imp) as fac_imp, sum(fac_cob) as fac_cob
from
(
--Suma dels costos imputats per albarans separant m d'obra i material
(select 'A' as tip ,alb_idf_cod as idf_cod, alb_emp_cod as emp_cod, alb_prj_num as prj_num,
 to_char(alb_dia,'YYYYMM') as anymes, sum(round(cost_material/alb_valdiveur,2)) as cos_mat, sum(round(cost_mobra/alb_valdiveur,2)) as cos_mob,
 0 as cost_prv_mat, 0 as cos_prv_mob, 0 as prv_fac, 0 as pre_cob, 0 as ava_pre, 0 as ava_est,
 0 as fac_imp, 0 as fac_cob
from vges_calculs_lac
having ( sum(cost_material) <> 0 or sum(cost_mobra) <> 0 )
group by alb_idf_cod, alb_emp_cod, alb_prj_num, to_char(alb_dia,'YYYYMM')
)
union all
(
--suma dels costos imputats per partes de feina que encara no s'han imputat a albarans
select 'P' as tip,lff_idf_cod as idf_cod,lff_emp_cod as emp_cod ,lff_prj_num as prj_num,
to_char(ffo_cln_dat,'YYYYMM') as anymes, 0 as cos_mat, sum(lff_hordec * lff_cstuni + nvl(lff_hordecmaq,0)*nvl(lff_cstmaq,0) + nvl(lff_hordecmaq002,0)*nvl(lff_cstmaq002,0)) as cos_mob,
0 as cost_prv_mat, 0 as cos_prv_mob, 0 as prv_fac, 0 as pre_cob, 0 as ava_pre, 0 as ava_est,
0 as fac_imp, 0 as fac_cob
from trhu_lff, trhu_ffo
where lff_idf_cod = ffo_idf_cod and lff_ffo_cod = ffo_cod AND ( LFF_LACNUMDOC IS NULL OR LFF_LACNUMDOC = 0)
group by lff_idf_cod ,lff_emp_cod, lff_prj_num,to_char(ffo_cln_dat,'YYYYMM')
)
union all
(
--suma de les previsions fetes per el projecte i l'avan indicat
select 'R' as tip, prp_idf_cod as idf_cod, prp_emp_cod as emp_cod, prp_prj_num as prj_num, prp_anymes as anymes, 0 as cos_mat, 0 as cost_mob,
round(prp_cosmat/nvl(prj_valdiveur,1),2) as cost_prv_mat, round(prp_cosmanobr/nvl(prj_valdiveur,1),2) as cos_prv_mob, round(prp_prtfac/nvl(prj_valdiveur,1),2) as prv_fac,
round(prp_prtcob/nvl(prj_valdiveur,1),2) as pre_cob, nvl(prp_ancprt,0) as ava_pre, nvl(prp_ancetm,0) as ava_est,
0 as fac_imp, 0 as fac_cob
from tges_prp, tges_prj
where prp_idf_cod = prj_idf_cod and prp_emp_cod = prj_emp_cod and prp_prj_num = prj_num
and prp_etp_cod='.' and prp_etp_num=0 /*mateur, 08/20/2017 - #59650*/
)
union all
(
--suma de la facturaci imputada al projecte i el cobros d'aquestes factures. Les dades de facturaci sn en Base Imposable
select 'F' as tip, fac_idf_cod as idf_cod, fac_emp_cod as emp_cod, fac_prj_num as prj_num, to_char(fac_dia,'YYYYMM') as anymes,
0 as cos_mat, 0 as cost_mob,0 as cost_prv_mat, 0 as cos_prv_mob, 0 as prv_fac, 0 as pre_cob, 0 as ava_pre, 0 as ava_est,
round(fges_impfac(fac_idf_cod,fac_emp_cod,fac_ser_cod,fac_num,fac_cls)/fac_valdiveur,2) as fac_imp,
sum(round(decode( fac_tot, 0, 0,(vcx_imp* fges_impfac(fac_idf_cod,fac_emp_cod,fac_ser_cod,fac_num,fac_cls)/fac_tot))/mdc_valdiveur,2)) as fac_cob
from tges_fac, tges_vcx, tges_mdc
where vcx_idf_cod(+) = fac_idf_cod and vcx_emp_cod(+) = fac_emp_cod and vcx_ser_cod(+) = fac_ser_cod and vcx_fac_num(+) = fac_num and vcx_fac_cls(+) = fac_cls
and vcx_idf_cod = mdc_idf_cod and vcx_emp_cod = mdc_emp_cod and vcx_cxa_cod = mdc_cxa_cod and vcx_mdc_num = mdc_num
group by  fac_idf_cod, fac_emp_cod, fac_prj_num , to_char(fac_dia,'YYYYMM'),fac_ser_cod, fac_num, fac_cls,fac_tot, fac_valdiveur
)
)
group by idf_cod, emp_cod, prj_num, anymes;


