CREATE OR REPLACE FORCE VIEW VGES_RELVEN
AS 
SELECT                                                         /*+ RULE*/
           fac_cls,
              fac_ser_cod,
              fac_num,
              ser_des,
              fac_dia,
              TO_CHAR (fac_dia, 'yyyy') anyo,
              TO_CHAR (fac_dia, 'mm') mes,
              TO_CHAR (fac_dia, 'dd') dia,
              far_des,
              DECODE (art_gma_cod, NULL, 'SIN SUBFAMILIA', gma_des) gma_des,
              art_descur,
              DECODE (fac_ser_cod,
                      NULL, alb_ser_cod || '/' || alb_num,
                      fac_ser_cod || '/' || fac_num)
                 factura,
              iva_pte,
              Fges_Fac_Pdtcob (fac_idf_cod,
                               fac_emp_cod,
                               fac_ser_cod,
                               fac_num,
                               fac_cls)
                 pdtcob,
              lac_uni lac_uni,
              DECODE (
                 lac_uni,
                 0,
                 0,
                 ROUND (
                    ROUND (
                         lac_imp
                       * (1 - NVL (fac_dto, 0) * 0.01)
                       * (1 - NVL (fac_dto002, 0) * 0.01),
                       2
                    )
                    / lac_uni,
                    2
                 )
              )
                 lac_pru,
              ROUND (
                   lac_imp
                 * (1 - NVL (fac_dto, 0) * 0.01)
                 * (1 - NVL (fac_dto002, 0) * 0.01),
                 2
              )
                 lac_imp,
              (SELECT   MAX (DPG_DES)
                 FROM   tges_vcx, tges_mdc, tges_dpg
                WHERE       vcx_idf_cod = fac_idf_cod
                        AND vcx_emp_cod = fac_emp_cod
                        AND vcx_fac_cls = fac_cls
                        AND vcx_fac_num = fac_num
                        AND vcx_ser_cod = fac_ser_cod
                        AND vcx_idf_cod = mdc_idf_cod
                        AND vcx_emp_cod = mdc_emp_cod
                        AND vcx_cxa_cod = mdc_cxa_cod
                        AND vcx_mdc_num = mdc_num
                        AND DPG_IDF_COD = MDC_IDF_COD
                        AND DPG_COD = MDC_DPG_COD)
                 fpg_des,
              alb_dia,
              alb_ser_cod,
              art_far_cod,
              art_gma_cod,
              lac_art_cod,
              alb_cls,
              alb_idf_cod,
              alb_emp_cod
       FROM   tges_alb,
              tges_lac,
              tges_fac,
              tges_art,
              tges_ser,
              tges_far,
              tges_gma,
              tges_iva
      WHERE       lac_iva_cod(+) = iva_cod
              AND lac_idf_cod(+) = iva_idf_cod
              AND far_idf_cod = art_idf_cod
              AND far_cod = art_far_cod
              AND art_idf_cod = gma_idf_cod(+)
              AND art_gma_cod = gma_cod(+)
              AND SER_IDF_COD = alb_idf_cod
              AND SER_EMP_COD = alb_emp_cod
              AND SER_COD = alb_ser_cod
              AND ( (    lac_alb_numdoc = alb_numdoc
                     AND lac_emp_cod = alb_emp_cod
                     AND lac_idf_cod = alb_idf_cod)
                   AND (    alb_fac_num = fac_num(+)
                        AND alb_fac_cls = fac_cls(+)
                        AND alb_ser_codfac = fac_ser_cod(+)
                        AND alb_emp_cod = fac_emp_cod(+)
                        AND alb_idf_cod = fac_idf_cod(+))
                   AND (lac_art_cod = art_cod AND lac_idf_cod = art_idf_cod))
   ORDER BY   fac_dia;


