AS 
select ffm_idf_cod,ffm_emp_cod,ffm_prj_num, prj_nom, nvl(prj_div_cod,ffm_div_cod) as div_cod,
ffm_dia, to_char(ffm_dia, 'YYYY') as ffm_any, to_char(ffm_dia,'MM') as ffm_mes,
