CREATE OR REPLACE FORCE VIEW VCMP_APUFAC
AS 
SELECT /*+INDEX(tcmp_apunts icmp_apu_contra)*/
apu_cec_cod, apu_emp_cod, apu_eje_cod, apu_dia_cod, apu_asi_numasi, apu_numapu,
