WHERE (apu_ct5_cod LIKE '472%' OR apu_ct5_cod LIKE '477%')
AND apu_iva_codint IS NOT NULL AND apu_civ_cod IS NOT NULL
AND apu_asi_dat >= TO_DATE(/*Fcmp_Par(apu_cec_cod, 'REGCRICXA_INI',*/ '01/01/2014'/*)*/, 'dd/mm/yyyy')
